mapper.select:
select ut.user_id, ut.username, ut.password, ut.age, ut.create_dt, ut.update_dt,
urt.user_role_id, urt.user_id as urt_user_id, urt.role_id as urt_role_id,
rt.role_id, rt.role_name, rt.role_name_kor
from user_tb ut join user_role_tb urt on (ut.user_id = urt.user_id) join role_tb rt on (rt.role_id = urt.role_id)
where username = #{username}

select ut.user_id, ut.username, ut.password, ut.age, ut.age, ut.create_dt, ut.update_dt,
urt.user_role_id, urt.user_id as urt_user_id, urt.role_id as urt_role_id,
rt.role_id, rt.role_name, rt.role_name_kor from user_tb ut join user_role_tb urt on (ut.user_id = urt.user_id) join role_tb rt on (rt.role_id = urt.role_id) where ut.user_id = #{userId};

mapper.update:
update
    user_tb
set
    password = #{newPassword}
where
    user_id = #{userId};
